from nacl.signing import VerifyKey
from nacl.exceptions import BadSignatureError
from supra_client.exception import *


def test_sign_raw_tx(cmd, button, model):
    message = bytes.fromhex(
        ""
    )
    bip32_path: str = "m/44'/637'/1'/0'/0'"

    pub_key, chain_code = cmd.get_public_key(
        bip32_path=bip32_path, display=False
    )  # type: bytes, bytes

    pk = VerifyKey(pub_key[1:])

    der_sig = cmd.sign_raw(
        bip32_path=bip32_path, data=message, button=button, model=model
    )

    try:
        pk.verify(signature=der_sig, smessage=message)
    except BadSignatureError as exc:
        assert False, exc
